mapper.insert:
INSERT INTO POSTS (
title, club_type, media, content, author, category

	,height
	,career
	,handy
	,ironDistance
	,driverDistance

)
VALUES (
#{title}, #{clubType}, #{media}, #{content}, #{author},
'P001'

	,#{height}
	,#{career}
	,#{handy}
	,#{ironDistance}
	,#{driverDistance}

)

mapper.select:
SELECT * FROM (
    SELECT a.*, ROWNUM rnum FROM (
        SELECT * FROM posts WHERE category IN('P001')
        ORDER BY id DESC
    ) a WHERE ROWNUM <= #{limit} + #{offset}
) WHERE rnum > #{offset}
