mapper.update:
update posts
set views = views + 1
where id = #{id}

update posts
set likes = likes + 1
where id = #{id}

mapper.select:
SELECT * FROM (
SELECT
A.*,
ROWNUM AS rnum
FROM (
SELECT *
FROM posts
WHERE category IN('F001' , 'F002' , 'F003')
ORDER BY created_time DESC
) A
WHERE ROWNUM <= #{endRow}
)
WHERE rnum > #{startRow}
